mapper.select:
SELECT inst_no, inst_nm, outsrc_yn
FROM inst
WHERE inst_no = #{instNo}

SELECT COUNT(*)
FROM inst
WHERE inst_no = #{instNo}

SELECT COUNT(*)
FROM usr
WHERE inst_no = #{instNo}

mapper.update:
UPDATE inst
SET inst_nm = #{instNm}
WHERE inst_no = #{instNo}

UPDATE inst
SET outsrc_yn = #{instClsf}
WHERE inst_no = #{instNo}

mapper.select:
SELECT count(*)
FROM usr
WHERE ibps_no = #{ibpsNo}

SELECT count(*)
FROM ibps
WHERE ibps_no = #{ibpsNo}

mapper.update:
UPDATE ibps
SET ibps_nm = #{ibpsNm}
WHERE ibps_no = #{ibpsNo}

mapper.select:
SELECT count(*)
FROM usr
WHERE role_no = #{roleNo}

SELECT count(*)
FROM role
WHERE role_no = #{roleNo}

mapper.update:
UPDATE role
SET role_nm = #{roleNm}, role_seq = #{roleSeq}
WHERE role_no = #{roleNo}

mapper.select:
SELECT count(*)
FROM usr
WHERE dept_no = #{deptNo}

SELECT count(*)
FROM dept
WHERE dept_no = #{deptNo}

mapper.update:
UPDATE dept
SET dept_nm = #{deptNm}
WHERE dept_no = #{deptNo}
