mapper.select:
SELECT sr_prgrs_stts_no
FROM sr_trnsf_plan
WHERE sr_no = #{srNo}

SELECT rnum, sr_no, sys_nm, sr_task_nm, sr_ttl, usr_nm, sr_cmptn_prnmnt_dt, sr_trgt_cmptn_dt, sr_prgrs_stts_nm FROM( SELECT rownum as rnum, sr_no, sys_nm, sr_task_nm, sr_ttl, usr_nm, sr_cmptn_prnmnt_dt, sr_trgt_cmptn_dt, sr_prgrs_stts_nm FROM ( SELECT sr.sr_no, sys.sys_nm, srtc.sr_task_nm, srr.sr_ttl, pic.usr_nm, sr.sr_cmptn_prnmnt_dt, srtp.sr_trgt_cmptn_dt, srps.sr_prgrs_stts_nm FROM sr JOIN sr_rqst srr ON sr.sr_rqst_no = srr.sr_rqst_no JOIN sr_trnsf_plan srtp ON sr.sr_no = srtp.sr_no JOIN usr pic ON sr.pic_usr_no = pic.usr_no JOIN usr pl ON srtp.usr_no = pl.usr_no JOIN sys ON srr.sys_no = sys.sys_no JOIN sr_task_clsf srtc ON sr.sr_task_no = srtc.sr_task_no JOIN sr_prgrs_stts srps ON srtp.sr_prgrs_stts_no = srps.sr_prgrs_stts_no LEFT JOIN sr_trnsf_hr srth ON sr.sr_no = srth.sr_no LEFT JOIN usr dvl ON srth.usr_no = dvl.usr_no WHERE (pl.usr_no = #{usrNo} OR dvl.usr_no = #{usrNo})
AND srtp.sr_prgrs_stts_no = #{srPrgrsSttsNo}
AND srr.sr_rqst_stts_no != 'DEP_CMPTN'
UNION SELECT sr.sr_no, sys.sys_nm, srtc.sr_task_nm, srr.sr_ttl, pic.usr_nm, sr.sr_cmptn_prnmnt_dt, srtp.sr_trgt_cmptn_dt, srps.sr_prgrs_stts_nm FROM sr JOIN sr_rqst srr ON sr.sr_rqst_no = srr.sr_rqst_no JOIN sr_trnsf_plan srtp ON sr.sr_no = srtp.sr_no JOIN usr pic ON sr.pic_usr_no = pic.usr_no JOIN sys ON srr.sys_no = sys.sys_no JOIN sr_task_clsf srtc ON sr.sr_task_no = srtc.sr_task_no JOIN sr_prgrs_stts srps ON srtp.sr_prgrs_stts_no = srps.sr_prgrs_stts_no LEFT JOIN sr_trnsf_hr srth ON sr.sr_no = srth.sr_no LEFT JOIN usr dvl ON srth.usr_no = dvl.usr_no WHERE srtp.sr_prgrs_stts_no = 'RQST' AND srr.sr_rqst_stts_no != 'DEP_CMPTN'
ORDER BY sr_no )
WHERE rownum <= #{pager.endRowNo} ) WHERE rnum >= #{pager.startRowNo}

SELECT rnum, dept_nm, role_nm, ibps_nm, usr_nm, usr_no
FROM(
	SELECT rownum as rnum, dept_nm, role_nm, ibps_nm, usr_nm, usr_no
	FROM (
		SELECT dept.dept_nm, role.role_nm, ibps.ibps_nm, usr2.usr_nm, usr2.usr_no
		FROM usr
		JOIN inst ON usr.inst_no = inst.inst_no
		JOIN usr usr2 ON inst.inst_no = usr2.inst_no
		JOIN dept ON usr2.dept_no = dept.dept_no
		JOIN role ON usr2.role_no = role.role_no
		JOIN ibps ON usr2.ibps_no = ibps.ibps_no
		WHERE usr.usr_no = #{usrNo}
		AND dept.dept_no = #{deptNo}
		AND usr2.usr_nm = #{usrNm}
		ORDER BY role.role_seq desc
	)

	WHERE rownum <= #{pager.endRowNo}
)
WHERE rnum >= #{pager.startRowNo}

SELECT COUNT(*)
FROM sr_trnsf_hr
WHERE sr_no = #{srNo} AND usr_no = #{usrNo}

